import React, { Component } from 'react'
import ReactDOM from 'react-dom'
import {withHistory} from 'react-router-dom'
import {withTracker} from 'meteor/react-meteor-data'
import Header from './header';
import Footer from './footer';

import {Courses} from '../../../api/courses.jsx'
import Course from '../../containers/Course.jsx'

//import backgroun from '../../../../client/assets/images/courses_background.jpg'


class Cours extends Component { 

  constructor(props) {
    super(props);

    this.state = {

    };
  }

handleSubmit(event) {
    event.preventDefault();

    // Find the text field via the React ref
    const text = ReactDOM.findDOMNode(this.refs.textInput).value.trim();
      console.log(text);
      Meteor.call('courses.insert', text); 
    // Clear form
    ReactDOM.findDOMNode(this.refs.textInput).value = '';
  }

  toggleHideCompleted() {
    this.setState({
      hideCompleted: !this.state.hideCompleted,
    });
  }

  sortCourses(event) {
    let x=event.target.value;
    //console.log(x); 
    this.setState({text: x })
    }

renderCourses() {
    let filteredCourses = this.props.courses;
    //console.log(this.state.text)
    if (this.state.text=="text") {
      filteredCourses = filteredCourses.sort((a,b)=>{
       return a.text.localeCompare(b.text)
      })
    }
    else if (this.state.text=="createdAt")
    {
      filteredCourses = filteredCourses.sort((a,b)=>{
        return b.createdAt-a.createdAt
      });
    }

    if (this.state.hideCompleted) {
      filteredCourses = filteredCourses.filter(course => !course.checked);
    }

    return (
      filteredCourses.map((course) => {
      const currentUserId = this.props.currentUser && this.props.currentUser._id;
      const showPrivateButton = course.owner === currentUserId;

      return (
        <Course
          key={course._id}
          course={course}
          showPrivateButton={showPrivateButton}
        />
      );
    })

    )
  }


  render(){
    return (
      <div className="super_container">

        <header>
          <h1>Courses </h1>
          <label className="hide-completed">
            <input
              type="checkbox"
              readOnly
              checked={this.state.hideCompleted}
              onClick={this.toggleHideCompleted.bind(this)}
            />
            Hide Completed Courses
          </label>


          <select onChange={e=> {this.sortCourses(e)}}>
            <option value="text">Nom</option>
            <option value="createdAt">Date de creation</option>
          </select>


          { this.props.currentUser ?
            <form className="new-course" onSubmit={this.handleSubmit.bind(this)} >
              <input
                type="text"
                ref="textInput"
                placeholder="Type to add new courses"
              />
            </form> : ''
          }          
        </header>

        <div>
          {this.renderCourses()}
        </div>



        {/*<Header/>*/}

        <div className="menu_container menu_mm">
          {/* Menu Close Button */}
          <div className="menu_close_container">
            <div className="menu_close" />
          </div>
          {/* Menu Items */}
          <div className="menu_inner menu_mm">
            <div className="menu menu_mm">
              <ul className="menu_list menu_mm">
                <li className="menu_item menu_mm">
                  <a href="index.html">Home</a>
                </li>
                <li className="menu_item menu_mm">
                  <a href="#">About us</a>
                </li>
                <li className="menu_item menu_mm">
                  <a href="#">Courses</a>
                </li>
                <li className="menu_item menu_mm">
                  <a href="elements.html">Elements</a>
                </li>
                <li className="menu_item menu_mm">
                  <a href="news.html">News</a>
                </li>
                <li className="menu_item menu_mm">
                  <a href="contact.html">Contact</a>
                </li>
              </ul>
              {/* Menu Social */}
              <div className="menu_social_container menu_mm">
                <ul className="menu_social menu_mm">
                  <li className="menu_social_item menu_mm">
                    <a href="#">
                      <i className="fab fa-pinterest" />
                    </a>
                  </li>
                  <li className="menu_social_item menu_mm">
                    <a href="#">
                      <i className="fab fa-linkedin-in" />
                    </a>
                  </li>
                  <li className="menu_social_item menu_mm">
                    <a href="#">
                      <i className="fab fa-instagram" />
                    </a>
                  </li>
                  <li className="menu_social_item menu_mm">
                    <a href="#">
                      <i className="fab fa-facebook-f" />
                    </a>
                  </li>
                  <li className="menu_social_item menu_mm">
                    <a href="#">
                      <i className="fab fa-twitter" />
                    </a>
                  </li>
                </ul>
              </div>
              <div className="menu_copyright menu_mm">
                Colorlib All rights reserved
              </div>
            </div>
          </div>
        </div>

        <div className="home">
          <div className="home_background_container prlx_parent">
            <div
              className="home_background prlx"
              style={{
                backgroundImage: "url(images/courses_background.jpg)"
              }}
            />
          </div>
          <div className="home_content">
            <h1>Courses</h1>
          </div>
        </div>

        <div className="popular page_section">
          <div className="container">
            <div className="row">
              <div className="col">
              </div>
            </div>
            <div className="row course_boxes">
              {/* Course Item */}
              <div className="col-lg-4 course_box">
                <div className="card">
                  <img
                    className="card-img-top"
                    src="images/course_9.jpg"
                    alt="https://unsplash.com/@kimberlyfarmer"
                  />  


                  <div className="card-body text-center">
                    <div className="card-title">
                      <a href="#">{this.props.courses.text}</a>
                    </div>
                    <div className="card-text">
                      {this.props.courses.text}
                    </div>
                  </div>
                  <div className="price_box d-flex flex-row align-items-center">
                    <div className="course_author_image">
                      <img
                        src="images/author.jpg"
                        alt="https://unsplash.com/@mehdizadeh"
                      />
                    </div>
                    <div className="course_author_name">
                      Michael Smith, <span>{this.props.courses.text}</span>
                    </div>

                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>

        <Footer/>


      </div>

    );
  }
}

export default withTracker(() => {
  Meteor.subscribe('courses');
  return {

    courses: Courses.find({}).fetch(),
    incompleteCount: Courses.find({ checked: { $ne: true } }).count(),
        currentUser: Meteor.user(),

  };
}) (Cours);
